[DEBUG]
debugger({ 
  target: "payment_integration",
  action: "analyze_current_state",
  parameters: {
    "project_structure": "work/",
    "focus": "payment implementation"
  }
})

[ANALYZE]
log_analyzer({
  target: "payment_system",
  action: "scan_for_errors",
  parameters: {
    "log_directory": "work/project-name/logs",
    "pattern": "payment|checkout|transaction",
    "max_results": 50
  }
})

[CHECK]
dependency_checker({
  target: "payment_dependencies",
  action: "validate_requirements",
  parameters: {
    "project_path": "work/project-name",
    "required_packages": ["stripe", "paypal", "payment-gateway"],
    "check_dev_dependencies": true
  }
})

[payment_api_client]
payment_api_client({
  target: "payment_gateway",
  action: "validate_connection",
  parameters: {
    "api_endpoint": "https://api.stripe.com/v1",
    "credentials_required": true,
    "test_mode": true
  }
})
