[FILE: src/components/Player.tsx]:


```tsx
import React, { useState, useEffect } from 'react';
import './Player.css';

const Player: React.FC = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);

  // Simular progresso da m√∫sica
  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (isPlaying) {
      interval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 100) {
            clearInterval(interval);
            setIsPlaying(false);
            return 0;
          }
          return prev + 0.5;
        });
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [isPlaying]);

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  return (
    <footer className="player">
      <div className="track-info">
        <img src="https://placehold.co/60" alt="Current track" className="track-art" />
        <div className="track-details">
          <div className="track-name">Current Track</div>
          <div className="artist-name">Artist Name</div>
        </div>
        <button className="like-btn">‚ù§Ô∏è</button>
      </div>
      <div className="player-controls">
        <button className="control-btn">‚èÆ</button>
        <button className="control-btn play-btn" onClick={togglePlay}>
          {isPlaying ? '‚è∏' : '‚ñ∂'}
        </button>
        <button className="control-btn">‚è≠</button>
      </div>
      <div className="progress-container">
        <span className="time">0:00</span>
        <div className="progress-bar" onClick={(e) => {
          const rect = e.currentTarget.getBoundingClientRect();
          const pos = (e.clientX - rect.left) / rect.width;
          setProgress(pos * 100);
        }}>
          <div 
            className="progress" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>
        <span className="time">3:00</span>
      </div>
    </footer>
  );
};

export default Player;
```

