/* src/index.css - Adicionando configurações responsivas */
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Configurações responsivas para mobile */
@media (max-width: 768px) {
  .container {
    @apply px-4;
  }

  .sidebar {
    @apply fixed inset-y-0 left-0 z-50 w-64 transform transition-transform duration-300 ease-in-out;
  }

  .sidebar.hidden {
    @apply -translate-x-full;
  }

  .main-content {
    @apply w-full transition-all duration-300 ease-in-out;
  }

  .playlist-card {
    @apply w-full h-auto;
  }

  .player-controls {
    @apply flex-col space-y-4;
  }

  .player-progress {
    @apply w-full;
  }

  .header-nav {
    @apply flex-col space-y-2;
  }

  .search-bar {
    @apply w-full;
  }

  .grid-cards {
    @apply grid-cols-1 gap-4;
  }
}

/* Media queries específicas para dispositivos móveis */
@media (max-width: 640px) {
  .mobile-menu-button {
    @apply block;
  }

  .desktop-nav {
    @apply hidden;
  }

  .mobile-header {
    @apply flex items-center justify-between p-4;
  }

  .player-bar {
    @apply flex-col space-y-2 p-2;
  }

  .player-info {
    @apply flex-col items-center text-center;
  }

  .player-actions {
    @apply flex-wrap justify-center;
  }
}

/* Ajustes para telas muito pequenas */
@media (max-width: 480px) {
  .playlist-card {
    @apply p-2;
  }

  .card-title {
    @apply text-sm;
  }

  .card-subtitle {
    @apply text-xs;
  }

  .button {
    @apply px-3 py-1.5 text-sm;
  }

  .player-volume {
    @apply w-24;
  }
}

/* Configurações para dispositivos com tela pequena e orientação paisagem */
@media (max-width: 768px) and (orientation: landscape) {
  .sidebar {
    @apply w-48;
  }

  .main-content {
    @apply ml-48;
  }
}

/* Ajustes para telas com densidade de pixels alta */
@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
  * {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
}

/* Configurações para touch devices */
@media (hover: none) and (pointer: coarse) {
  .hover\:bg-opacity-80 {
    @apply bg-opacity-80 !important;
  }

  .hover\:scale-105 {
    @apply scale-100 !important;
  }
}

/* Ajustes para telas com tamanho muito pequeno */
@media (max-width: 320px) {
  .container {
    @apply px-2;
  }

  .text-lg {
    @apply text-base;
  }

  .text-xl {
    @apply text-lg;
  }

  .text-2xl {
    @apply text-xl;
  }
}
```

