import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './components/Layout';
import './App.css';

function App() {
  return (
    <Router>
      <div className="App h-screen overflow-hidden">
        <Layout>
          <Routes>
            <Route path="/" element={
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {/* Cards de exemplo */}
                {[...Array(12)].map((_, index) => (
                  <div 
                    key={index} 
                    className="bg-gray-800 rounded-lg p-4 hover:bg-gray-700 transition-all duration-200 cursor-pointer"
                  >
                    <div className="bg-gray-200 border-2 border-dashed rounded-xl w-full h-48 mb-4" />
                    <h3 className="font-semibold">Playlist {index + 1}</h3>
                    <p className="text-gray-400 text-sm">Descrição da playlist</p>
                  </div>
                ))}
              </div>
            } />
          </Routes>
        </Layout>
      </div>
    </Router>
  );
}

export default App;
