```tsx
// src/components/Player.tsx
import React, { useState } from 'react';
import { FaPlay, FaPause, FaStepBackward, FaStepForward, FaVolumeUp, FaHeart, FaRegHeart, FaList } from 'react-icons/fa';

const Player: React.FC = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [volume, setVolume] = useState(80);

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  const toggleLike = () => {
    setIsLiked(!isLiked);
  };

  return (
    <div className="fixed bottom-0 left-0 right-0 h-20 bg-gray-900 border-t border-gray-800 flex items-center px-4 z-50">
      <div className="flex items-center w-full max-w-2xl">
        {/* Informações da música */}
        <div className="flex items-center w-1/4">
          <div className="bg-gray-200 border-2 border-dashed rounded-xl w-14 h-14 mr-3" />
          <div>
            <div className="font-medium text-sm truncate max-w-[120px]">Nome da Música</div>
            <div className="text-gray-400 text-xs">Artista</div>
          </div>
          <button 
            onClick={toggleLike}
            className="ml-4 text-gray-400 hover:text-white"
          >
            {isLiked ? <FaHeart className="text-green-500" /> : <FaRegHeart />}
          </button>
        </div>

        {/* Controles de reprodução */}
        <div className="flex flex-col items-center w-2/4">
          <div className="flex items-center space-x-4">
            <button className="text-gray-400 hover:text-white">
              <FaStepBackward />
            </button>
            <button 
              onClick={togglePlay}
              className="bg-white text-black rounded-full p-2 hover:scale-105 transition-transform"
            >
              {isPlaying ? <FaPause /> : <FaPlay className="ml-1" />}
            </button>
            <button className="text-gray-400 hover:text-white">
              <FaStepForward />
            </button>
          </div>
          <div className="w-full flex items-center mt-1">
            <span className="text-xs text-gray-400 mr-2">0:00</span>
            <div className="flex-1 h-1 bg-gray-600 rounded-full">
              <div className="h-1 bg-gray-200 rounded-full w-1/3"></div>
            </div>
            <span className="text-xs text-gray-400 ml-2">3:45</span>
          </div>
        </div>

        {/* Controles de volume e outros */}
        <div className="flex items-center justify-end w-1/4">
          <button className="text-gray-400 hover:text-white mx-2">
            <FaList />
          </button>
          <div className="flex items-center">
            <FaVolumeUp className="text-gray-400 mr-2" />
            <input
              type="range"
              min="0"
              max="100"
              value={volume}
              onChange={(e) => setVolume(parseInt(e.target.value))}
              className="w-20 accent-green-500"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Player;
```

