// src/App.tsx
import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import { mockData } from './data/mockData';

function App() {
  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 1024);
  const [isSidebarExpanded, setIsSidebarExpanded] = useState(true);

  useEffect(() => {
    const handleResize = () => {
      const isDesktopView = window.innerWidth >= 1024;
      setIsDesktop(isDesktopView);

      // Para desktop, manter sidebar sempre expandida
      if (isDesktopView) {
        setIsSidebarExpanded(true);
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Verificar tamanho inicial

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="App">
      <Sidebar isDesktop={isDesktop} />

      <main className={`main-content transition-all duration-300 ${
        isDesktop ? (isSidebarExpanded ? 'ml-60 md:ml-72 lg:ml-80' : 'ml-20') : 'ml-0'
      }`}>
        <header className="header bg-black text-white h-16 flex items-center px-6 border-b border-gray-800">
          <h1 className="text-xl font-bold">Descobrir</h1>
        </header>

        <div className="music-grid">
          {mockData.map((item, index) => (
            <div 
              key={index} 
              className="music-card bg-gray-800 rounded-lg p-4 hover:bg-gray-700 transition-colors duration-200 cursor-pointer"
            >
              <img 
                src={item.image || 'https://placehold.co/200x200'} 
                alt={item.title} 
                className="w-full aspect-square object-cover rounded-md mb-3"
              />
              <h3 className="font-semibold text-white truncate">{item.title}</h3>
              <p className="text-gray-400 text-sm truncate">{item.artist}</p>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
