// src/components/Sidebar.tsx
import React, { useState, useEffect } from 'react';
import { FiHome, FiSearch, FiPlus, FiHeart, FiMusic, FiUser, FiMenu, FiX } from 'react-icons/fi';

interface SidebarProps {
  isDesktop?: boolean;
}

const Sidebar: React.FC<SidebarProps> = ({ isDesktop = true }) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [activeItem, setActiveItem] = useState('home');
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const menuItems = [
    { id: 'home', label: 'Início', icon: FiHome },
    { id: 'search', label: 'Buscar', icon: FiSearch },
    { id: 'library', label: 'Sua Biblioteca', icon: FiMusic },
    { id: 'playlists', label: 'Playlists', icon: FiPlus },
    { id: 'liked', label: 'Músicas Curtidas', icon: FiHeart },
    { id: 'profile', label: 'Perfil', icon: FiUser },
  ];

  useEffect(() => {
    const handleResize = () => {
      const width = window.innerWidth;
      setWindowWidth(width);

      // Em desktop, manter sidebar sempre aberta
      if (width >= 1024) {
        setIsSidebarOpen(true);
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize();

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleSidebar = () => {
    if (!isDesktop) {
      setIsSidebarOpen(!isSidebarOpen);
    }
  };

  const handleItemClick = (id: string) => {
    setActiveItem(id);
  };

  // Determinar classes baseadas no estado e tamanho da tela
  const sidebarClasses = [
    'fixed top-0 left-0 h-full bg-gray-900 text-white z-40 transition-all duration-300 ease-in-out',
    isDesktop 
      ? 'w-60 md:w-72 lg:w-80'  // Larguras diferentes para desktop
      : isSidebarOpen 
        ? 'translate-x-0 w-60' 
        : '-translate-x-full w-60'
  ].join(' ');

  return (
    <>
      {/* Botão de menu para mobile/tablet */}
      {!isDesktop && (
        <div className="fixed top-4 left-4 z-50 lg:hidden">
          <button 
            onClick={toggleSidebar}
            className="p-2 rounded-full bg-gray-800 text-white"
          >
            {isSidebarOpen ? <FiX size={20} /> : <FiMenu size={20} />}
          </button>
        </div>
      )}

      {/* Sidebar */}
      <aside className={sidebarClasses}>
        <div className="p-6 border-b border-gray-800">
          <h1 className="text-2xl font-bold text-white">Music App</h1>
        </div>

        <nav className="mt-8">
          <ul>
            {menuItems.map((item) => {
              const Icon = item.icon;
              return (
                <li key={item.id}>
                  <button
                    onClick={() => handleItemClick(item.id)}
                    className={`w-full text-left p-3 flex items-center space-x-3 transition-colors duration-200 ${
                      activeItem === item.id 
                        ? 'bg-gray-700 text-white font-semibold' 
                        : 'hover:bg-gray-800 text-gray-300'
                    }`}
                  >
                    <Icon size={20} />
                    <span>{item.label}</span>
                  </button>
                </li>
              );
            })}
          </ul>
        </nav>

        <div className="p-6 mt-8">
          <h2 className="text-lg font-semibold mb-4 text-white">Playlists</h2>
          <ul className="space-y-2">
            {Array.from({ length: 5 }).map((_, index) => (
              <li 
                key={index} 
                className="text-gray-400 hover:text-white cursor-pointer p-2 hover:bg-gray-800 rounded transition-colors duration-200"
              >
                Playlist {index + 1}
              </li>
            ))}
          </ul>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
