import { FaPlay, FaPause, FaStepBackward, FaStepForward, FaVolumeUp } from 'react-icons/fa';

interface Song {
  id: string;
  title: string;
  artist: string;
  album: string;
  duration: number; // em segundos
  src: string;
}

interface PlayerProps {
  currentSong: Song | null;
  onPrevious: () => void;
  onNext: () => void;
}

const Player: React.FC<PlayerProps> = ({ currentSong, onPrevious, onNext }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [volume, setVolume] = useState(0.7);
  const audioRef = useRef<HTMLAudioElement>(null);

  useEffect(() => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.play().catch(error => console.error('Erro ao reproduzir áudio:', error));
      } else {
        audioRef.current.pause();
      }
    }
  }, [isPlaying, currentSong]);

  useEffect(() => {
    if (currentSong && audioRef.current) {
      audioRef.current.src = currentSong.src;
      setProgress(0);

      // Tenta reproduzir automaticamente quando a música muda
      if (isPlaying) {
        audioRef.current.play().catch(error => console.error('Erro ao reproduzir nova música:', error));
      }
    }
  }, [currentSong]);

  const handleTimeUpdate = () => {
    if (audioRef.current) {
      const currentTime = audioRef.current.currentTime;
      const duration = audioRef.current.duration || currentSong?.duration || 0;
      setProgress((currentTime / duration) * 100 || 0);
    }
  };

  const handleSeek = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newTime = parseFloat(e.target.value);
    setProgress(newTime);

    if (audioRef.current) {
      const duration = audioRef.current.duration || currentSong?.duration || 0;
      audioRef.current.currentTime = (newTime / 100) * duration;
    }
  };

  const handleVolumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);

    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const formatTime = (seconds: number): string => {
    if (isNaN(seconds)) return '0:00';

    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  const currentTime = audioRef.current ? audioRef.current.currentTime : 0;
  const duration = currentSong ? currentSong.duration : 0;

  return (
    <div className="bg-gray-800 text-white p-4 rounded-lg shadow-lg">
      <div className="flex items-center justify-between mb-4">
        {currentSong ? (
          <div className="flex-1 min-w-0">
            <h3 className="font-semibold text-lg truncate">{currentSong.title}</h3>
            <p className="text-gray-400 truncate">{currentSong.artist} - {currentSong.album}</p>
          </div>
        ) : (
          <div className="flex-1">
            <h3 className="font-semibold text-lg">Nenhuma música selecionada</h3>
            <p className="text-gray-400">Selecione uma música para reproduzir</p>
          </div>
        )}
      </div>

      <div className="mb-4">
        <input
          type="range"
          min="0"
          max="100"
          value={progress}
          onChange={handleSeek}
          className="w-full h-1.5 bg-gray-600 rounded-lg appearance-none cursor-pointer"
        />
        <div className="flex justify-between text-xs text-gray-400 mt-1">
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
      </div>

      <div className="flex items-center justify-center space-x-6">
        <button 
          onClick={onPrevious}
          className="text-gray-400 hover:text-white transition-colors"
          aria-label="Música anterior"
        >
          <FaStepBackward size={20} />
        </button>

        <button 
          onClick={togglePlayPause}
          className="bg-blue-600 hover:bg-blue-700 text-white rounded-full p-3 transition-colors"
          aria-label={isPlaying ? 'Pausar' : 'Tocar'}
        >
          {isPlaying ? <FaPause size={20} /> : <FaPlay size={20} className="ml-1" />}
        </button>

        <button 
          onClick={onNext}
          className="text-gray-400 hover:text-white transition-colors"
          aria-label="Próxima música"
        >
          <FaStepForward size={20} />
        </button>
      </div>

      <div className="flex items-center mt-4 space-x-2">
        <FaVolumeUp className="text-gray-400" />
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={handleVolumeChange}
          className="w-full h-1.5 bg-gray-600 rounded-lg appearance-none cursor-pointer"
        />
      </div>

      {/* Elemento de áudio oculto */}
      <audio
        ref={audioRef}
        onTimeUpdate={handleTimeUpdate}
        onEnded={() => setIsPlaying(false)}
        onLoadedMetadata={() => {
          if (audioRef.current && !audioRef.current.duration) {
            // Atualiza a duração se não estiver definida
            if (currentSong) {
              audioRef.current.duration = currentSong.duration;
            }
          }
        }}
      />
    </div>
  );
};

export default Player;"}}
