```typescript
// src/components/Player.tsx
import React, { useState, useEffect, useRef } from 'react';
import { FaPlay, FaPause, FaStepForward, FaStepBackward, FaRandom, FaRedo, FaVolumeUp } from 'react-icons/fa';

const Player = () => {
  // Estados para controle de reprodução
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(180); // Duração padrão de 3 minutos (180 segundos)
  const [volume, setVolume] = useState(0.7);
  const [isShuffled, setIsShuffled] = useState(false);
  const [isRepeated, setIsRepeated] = useState(false);

  // Referência para o intervalo de tempo
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  // Função para alternar entre play e pause
  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  // Função para avançar faixa
  const nextTrack = () => {
    // Resetar tempo da faixa atual
    setCurrentTime(0);
    console.log('Próxima faixa');
  };

  // Função para voltar faixa
  const prevTrack = () => {
    // Resetar tempo da faixa atual
    setCurrentTime(0);
    console.log('Faixa anterior');
  };

  // Efeito para controlar o progresso da música
  useEffect(() => {
    if (isPlaying) {
      // Iniciar o intervalo para atualizar o tempo
      intervalRef.current = setInterval(() => {
        setCurrentTime(prevTime => {
          if (prevTime >= duration) {
            if (isRepeated) {
              // Se repetição estiver ativada, voltar ao início
              return 0;
            } else {
              // Senão, pausar a reprodução
              setIsPlaying(false);
              return duration;
            }
          }
          return prevTime + 1;
        });
      }, 1000);
    } else if (intervalRef.current) {
      // Limpar o intervalo quando pausar
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }

    // Limpar o intervalo quando o componente for desmontado
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isPlaying, duration, isRepeated]);

  // Função para pular para um tempo específico
  const handleSeek = (e: React.MouseEvent<HTMLDivElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const percent = (e.clientX - rect.left) / rect.width;
    const newTime = percent * duration;
    setCurrentTime(newTime);
  };

  // Função para ajustar volume
  const handleVolumeChange = (e: React.MouseEvent<HTMLDivElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const percent = (e.clientX - rect.left) / rect.width;
    const newVolume = Math.min(1, Math.max(0, percent));
    setVolume(newVolume);
  };

  // Função para formatar tempo (segundos para MM:SS)
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  return (
    <div className="fixed bottom-0 left-0 right-0 bg-gray-900 border-t border-gray-800 p-4 z-50">
      <div className="flex items-center justify-between max-w-screen-xl mx-auto">
        {/* Informações da música atual */}
        <div className="flex items-center space-x-3 w-1/4">
          <img 
            src="https://placehold.co/60x60" 
            alt="Capa do álbum" 
            className="w-14 h-14 rounded"
          />
          <div>
            <h4 className="text-white text-sm font-medium">Nome da Música</h4>
            <p className="text-gray-400 text-xs">Artista</p>
          </div>
        </div>

        {/* Controles de reprodução */}
        <div className="flex flex-col items-center w-2/4">
          <div className="flex items-center space-x-6">
            <button 
              onClick={() => setIsShuffled(!isShuffled)}
              className={`text-gray-400 hover:text-white transition-colors ${isShuffled ? 'text-green-500' : ''}`}
              aria-label={isShuffled ? "Desativar aleatório" : "Ativar aleatório"}
            >
              <FaRandom size={16} />
            </button>
            <button 
              onClick={prevTrack}
              className="text-gray-400 hover:text-white transition-colors"
              aria-label="Faixa anterior"
            >
              <FaStepBackward size={16} />
            </button>
            <button 
              onClick={togglePlayPause}
              className="bg-white text-black rounded-full p-2 hover:scale-105 transition-transform"
              aria-label={isPlaying ? "Pausar" : "Tocar"}
            >
              {isPlaying ? <FaPause size={16} /> : <FaPlay size={16} className="ml-1" />}
            </button>
            <button 
              onClick={nextTrack}
              className="text-gray-400 hover:text-white transition-colors"
              aria-label="Próxima faixa"
            >
              <FaStepForward size={16} />
            </button>
            <button 
              onClick={() => setIsRepeated(!isRepeated)}
              className={`text-gray-400 hover:text-white transition-colors ${isRepeated ? 'text-green-500' : ''}`}
              aria-label={isRepeated ? "Desativar repetição" : "Ativar repetição"}
            >
              <FaRedo size={16} />
            </button>
          </div>

          {/* Barra de progresso */}
          <div className="flex items-center w-full mt-3 space-x-2">
            <span className="text-gray-400 text-xs w-10">{formatTime(currentTime)}</span>
            <div 
              className="flex-1 h-1 bg-gray-600 rounded-full cursor-pointer relative"
              onClick={handleSeek}
            >
              <div 
                className="h-full bg-gray-300 rounded-full absolute top-0 left-0"
                style={{ width: `${duration ? (currentTime / duration) * 100 : 0}%` }}
              ></div>
              {/* Indicador de posição */}
              <div 
                className="absolute top-1/2 w-3 h-3 bg-white rounded-full transform -translate-y-1/2"
                style={{ left: `${duration ? (currentTime / duration) * 100 : 0}%` }}
              ></div>
            </div>
            <span className="text-gray-400 text-xs w-10">{formatTime(duration)}</span>
          </div>
        </div>

        {/* Controles de volume */}
        <div className="flex items-center space-x-2 w-1/4 justify-end">
          <FaVolumeUp className="text-gray-400" size={16} />
          <div 
            className="w-24 h-1 bg-gray-600 rounded-full cursor-pointer relative"
            onClick={handleVolumeChange}
          >
            <div 
              className="h-full bg-gray-300 rounded-full absolute top-0 left-0"
              style={{ width: `${volume * 100}%` }}
            ></div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Player;
```

