            </div>

            {/* Progress Bar */}
            <div className="flex items-center w-full max-w-md space-x-2">
              <span className="text-gray-400 text-xs w-10">{formatTime(progress)}</span>
              <div 
                ref={progressRef}
                onClick={handleProgressClick}
                className="flex-1 h-1 bg-gray-600 rounded-full cursor-pointer relative"
              >
                <div 
                  className="absolute top-0 left-0 h-full bg-gray-300 rounded-full"
                  style={{ width: `${duration ? (progress / duration) * 100 : 0}%` }}
                >
                  <div className="absolute right-0 top-1/2 transform -translate-y-1/2 w-3 h-3 bg-white rounded-full"></div>
