import { useState, useEffect, useRef } from 'react';

interface Song {
  id: string;
  title: string;
  artist: string;
  album: string;
  duration: string;
  cover: string;
}

interface UsePlayerReturn {
  currentSong: Song | null;
  isPlaying: boolean;
  progress: number;
  duration: number;
  volume: number;
  repeat: boolean;
  shuffle: boolean;
  playSong: (song: Song) => void;
  togglePlayPause: () => void;
  nextSong: () => void;
  previousSong: () => void;
  setVolume: (volume: number) => void;
  seek: (time: number) => void;
  toggleRepeat: () => void;
  toggleShuffle: () => void;
  playlist: Song[];
  currentIndex: number;
}

export const usePlayer = (initialPlaylist: Song[]): UsePlayerReturn => {
  const [playlist] = useState<Song[]>(initialPlaylist);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentSong, setCurrentSong] = useState<Song | null>(initialPlaylist[0] || null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(0.7);
  const [repeat, setRepeat] = useState(false);
  const [shuffle, setShuffle] = useState(false);

  const audioRef = useRef<HTMLAudioElement | null>(null);
  const progressInterval = useRef<NodeJS.Timeout | null>(null);

  // Simular duração da música (em segundos)
  useEffect(() => {
    if (currentSong) {
      // Converter duração do formato MM:SS para segundos
      const [minutes, seconds] = currentSong.duration.split(':').map(Number);
      const totalSeconds = minutes * 60 + seconds;
      setDuration(totalSeconds);
    }
  }, [currentSong]);

  // Gerenciar o estado de reprodução
  useEffect(() => {
    if (isPlaying) {
      progressInterval.current = setInterval(() => {
        setProgress(prev => {
          if (prev >= duration) {
            handleSongEnd();
            return 0;
          }
          return prev + 1;
        });
      }, 1000);
    } else if (progressInterval.current) {
      clearInterval(progressInterval.current);
    }

    return () => {
      if (progressInterval.current) {
        clearInterval(progressInterval.current);
      }
    };
  }, [isPlaying, duration]);

  const handleSongEnd = () => {
    if (repeat) {
      setProgress(0);
      setIsPlaying(true);
    } else {
      nextSong();
    }
  };

  const playSong = (song: Song) => {
    setCurrentSong(song);
    setProgress(0);
    setIsPlaying(true);
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const nextSong = () => {
    if (shuffle) {
      // Selecionar aleatoriamente uma música diferente
      let newIndex: number;
      do {
        newIndex = Math.floor(Math.random() * playlist.length);
      } while (newIndex === currentIndex && playlist.length > 1);

      setCurrentIndex(newIndex);
      setCurrentSong(playlist[newIndex]);
      setProgress(0);
    } else {
      const nextIndex = (currentIndex + 1) % playlist.length;
      setCurrentIndex(nextIndex);
      setCurrentSong(playlist[nextIndex]);
      setProgress(0);
    }
  };

  const previousSong = () => {
    if (progress > 3) {
      // Se já passou de 3 segundos, volta para o início da música
      setProgress(0);
    } else {
      const prevIndex = (currentIndex - 1 + playlist.length) % playlist.length;
      setCurrentIndex(prevIndex);
      setCurrentSong(playlist[prevIndex]);
      setProgress(0);
    }
  };

  const setVolumeHandler = (volume: number) => {
    setVolume(volume);
  };

  const seek = (time: number) => {
    setProgress(time);
  };

  const toggleRepeat = () => {
    setRepeat(!repeat);
  };

  const toggleShuffle = () => {
    setShuffle(!shuffle);
  };

  return {
    currentSong,
    isPlaying,
    progress,
    duration,
    volume,
    repeat,
    shuffle,
    playSong,
    togglePlayPause,
    nextSong,
    previousSong,
    setVolume: setVolumeHandler,
    seek,
    toggleRepeat,
    toggleShuffle,
    playlist,
    currentIndex
  };
};
EOF
