        switch(e.key) {
          case '1':
            e.preventDefault();
            scrollToSection('home');
            break;
          case '2':
            e.preventDefault();
            scrollToSection('browse');
            break;
          case '3':
            e.preventDefault();
            scrollToSection('library');
            break;
          case '4':
            e.preventDefault();
            scrollToSection('playlists');
            break;
